import { combineReducers } from 'redux'
import { cakeReducer } from "./cake/CakeReducer";
import { icecreamReducer } from './iceCream/IceCreamReducer';

export const rootReducer = combineReducers ({
    cake: cakeReducer,
    iceCream: icecreamReducer
})
